class Sql_executor(object):
    def __init__(self, user_id, cursor):
        """ Все действия с sql """
        self.user_id = user_id
        self.cursor = cursor

    def add(self, table_name):
        add_str = 'INSERT INTO ' + table_name + ' VALUES({})'.format(str(self.user_id))
        self.cursor.execute(add_str)

    def delete(self, table_name):
        del_str = 'DELETE FROM ' + table_name + ' WHERE vk_id = {}'.format(str(self.user_id))
        self.cursor.execute(del_str)

    def select(self, table_name, condition=''):
        select_str = 'SELECT DISTINCT vk_id FROM ' + table_name + condition
        self.cursor.execute(select_str)
